'use client';

import { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { CheckCircle2, XCircle, Clock, Eye, Mail, Phone } from 'lucide-react';
import Link from 'next/link';
import Image from 'next/image';

interface SignedDevis {
  id: string;
  devisNumber: string;
  clientInfo: {
    name: string;
    email: string;
    company?: string;
    phone?: string;
  };
  siteType: string;
  budget: string;
  signedAt: string;
  status: 'signed' | 'accepted' | 'rejected';
  signature: string;
}

export default function AdminDevisPage() {
  const [devisList, setDevisList] = useState<SignedDevis[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadSignedDevis();
  }, []);

  const loadSignedDevis = async () => {
    try {
      // En production, ceci ferait un appel API
      // Pour les tests, on simule avec des données statiques
      const mockDevis: SignedDevis[] = [
        {
          id: 'devis-001',
          devisNumber: '2025-0124-001',
          clientInfo: {
            name: 'Jean Dupont',
            email: 'jean.dupont@email.com',
            company: 'Entreprise ABC',
            phone: '06 12 34 56 78'
          },
          siteType: 'Site Multi-pages Professionnel',
          budget: '1150€ HT',
          signedAt: new Date().toISOString(),
          status: 'signed',
          signature: 'data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAADUlEQVR42mNkYPhfDwAChwGA60e6kgAAAABJRU5ErkJggg=='
        },
        {
          id: 'devis-002',
          devisNumber: '2025-0123-002',
          clientInfo: {
            name: 'Marie Martin',
            email: 'marie.martin@email.com',
            company: 'Startup XYZ'
          },
          siteType: 'Application Web',
          budget: '2500€ HT',
          signedAt: new Date(Date.now() - 86400000).toISOString(), // Hier
          status: 'accepted',
          signature: 'data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAADUlEQVR42mNkYPhfDwAChwGA60e6kgAAAABJRU5ErkJggg=='
        }
      ];

      setDevisList(mockDevis);
    } catch (error) {
      console.error('Erreur lors du chargement des devis:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleAcceptDevis = async (devisId: string) => {
    try {
      // En production, appel API pour accepter
      console.log('Acceptation du devis:', devisId);

      // Mettre à jour localement pour les tests
      setDevisList(prev => 
        prev.map(devis => 
          devis.id === devisId 
            ? { ...devis, status: 'accepted' as const }
            : devis
        )
      );

      alert('Devis accepté ! Le client va recevoir une confirmation.');
    } catch (error) {
      console.error('Erreur lors de l\'acceptation:', error);
      alert('Erreur lors de l\'acceptation du devis.');
    }
  };

  const handleRejectDevis = async (devisId: string) => {
    const reason = prompt('Raison du refus (optionnel):');

    try {
      // En production, appel API pour refuser
      console.log('Refus du devis:', devisId, 'Raison:', reason);

      // Mettre à jour localement pour les tests
      setDevisList(prev => 
        prev.map(devis => 
          devis.id === devisId 
            ? { ...devis, status: 'rejected' as const }
            : devis
        )
      );

      alert('Devis refusé. Le client va recevoir une notification.');
    } catch (error) {
      console.error('Erreur lors du refus:', error);
      alert('Erreur lors du refus du devis.');
    }
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'signed':
        return <Badge variant="secondary" className="bg-yellow-100 text-yellow-800">En attente</Badge>;
      case 'accepted':
        return <Badge variant="secondary" className="bg-green-100 text-green-800">Accepté</Badge>;
      case 'rejected':
        return <Badge variant="secondary" className="bg-red-100 text-red-800">Refusé</Badge>;
      default:
        return <Badge variant="secondary">{status}</Badge>;
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
          <h2 className="text-xl font-bold mb-2">Chargement des devis...</h2>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background py-8">
      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="mb-8">
          <h1 className="text-3xl font-bold mb-2">Administration - Devis Signés</h1>
          <p className="text-gray-600">
            Gérez les devis signés par vos clients
          </p>
        </div>

        {devisList.length === 0 ? (
          <Card>
            <CardContent className="p-8 text-center">
              <Clock className="w-12 h-12 text-gray-400 mx-auto mb-4" />
              <h3 className="text-xl font-semibold mb-2">Aucun devis signé</h3>
              <p className="text-gray-600">
                Les devis signés par vos clients apparaîtront ici.
              </p>
            </CardContent>
          </Card>
        ) : (
          <div className="space-y-6">
            {devisList.map((devis) => (
              <Card key={devis.id} className="overflow-hidden">
                <CardHeader className="bg-gray-50">
                  <div className="flex justify-between items-start">
                    <div>
                      <CardTitle className="text-xl">
                        Devis #{devis.devisNumber}
                      </CardTitle>
                      <p className="text-gray-600 mt-1">
                        {devis.siteType} - {devis.budget}
                      </p>
                    </div>
                    <div className="text-right">
                      {getStatusBadge(devis.status)}
                      <p className="text-sm text-gray-500 mt-1">
                        Signé le {new Date(devis.signedAt).toLocaleDateString('fr-FR')}
                      </p>
                    </div>
                  </div>
                </CardHeader>

                <CardContent className="p-6">
                  <div className="grid md:grid-cols-2 gap-6">
                    {/* Informations client */}
                    <div>
                      <h4 className="font-semibold mb-3">Informations Client</h4>
                      <div className="space-y-2">
                        <p><strong>Nom :</strong> {devis.clientInfo.name}</p>
                        {devis.clientInfo.company && (
                          <p><strong>Entreprise :</strong> {devis.clientInfo.company}</p>
                        )}
                        <div className="flex items-center">
                          <Mail className="w-4 h-4 mr-2 text-gray-500" />
                          <a 
                            href={`mailto:${devis.clientInfo.email}`}
                            className="text-primary hover:underline"
                          >
                            {devis.clientInfo.email}
                          </a>
                        </div>
                        {devis.clientInfo.phone && (
                          <div className="flex items-center">
                            <Phone className="w-4 h-4 mr-2 text-gray-500" />
                            <a 
                              href={`tel:${devis.clientInfo.phone}`}
                              className="text-primary hover:underline"
                            >
                              {devis.clientInfo.phone}
                            </a>
                          </div>
                        )}
                      </div>
                    </div>

                    {/* Signature */}
                    <div>
                      <h4 className="font-semibold mb-3">Signature Électronique</h4>
                      <div className="border border-gray-200 rounded-lg p-4 bg-white">
                        <Image 
                          src={devis.signature} 
                          alt="Signature du client"
                          className="max-w-full h-auto border border-gray-300 rounded"
                          style={{ maxHeight: '100px' }}
                          width={200}
                          height={100}
                        />
                        <p className="text-xs text-gray-500 mt-2">
                          Signée le {new Date(devis.signedAt).toLocaleString('fr-FR')}
                        </p>
                      </div>
                    </div>
                  </div>

                  {/* Actions */}
                  <div className="mt-6 pt-6 border-t">
                    <div className="flex flex-wrap gap-3">
                      <Button variant="outline" size="sm" asChild>
                        <Link href={`/admin/devis/${devis.id}`}>
                          <Eye className="w-4 h-4 mr-2" />
                          Voir le détail
                        </Link>
                      </Button>

                      {devis.status === 'signed' && (
                        <>
                          <Button 
                            size="sm"
                            onClick={() => handleAcceptDevis(devis.id)}
                            className="bg-green-600 hover:bg-green-700"
                          >
                            <CheckCircle2 className="w-4 h-4 mr-2" />
                            Accepter le Projet
                          </Button>

                          <Button 
                            variant="destructive"
                            size="sm"
                            onClick={() => handleRejectDevis(devis.id)}
                          >
                            <XCircle className="w-4 h-4 mr-2" />
                            Refuser le Projet
                          </Button>
                        </>
                      )}

                      {devis.status === 'accepted' && (
                        <Badge variant="secondary" className="bg-green-100 text-green-800">
                          ✅ Projet accepté - En cours de développement
                        </Badge>
                      )}

                      {devis.status === 'rejected' && (
                        <Badge variant="secondary" className="bg-red-100 text-red-800">
                          ❌ Projet refusé
                        </Badge>
                      )}
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        )}

        <div className="mt-8 text-center">
          <Button variant="outline" asChild>
            <Link href="/">
              Retour à l'accueil
            </Link>
          </Button>
        </div>
      </div>
    </div>
  );
}

'use client';

import { useParams, useRouter } from 'next/navigation';
import { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Separator } from '@/components/ui/separator';
import { Checkbox } from '@/components/ui/checkbox';
import { SignaturePad } from '@/components/SignaturePad';
import { CheckCircle2, FileText, Clock, AlertCircle } from 'lucide-react';
import Link from 'next/link';

interface DevisData {
  id: string;
  devisNumber: string;
  clientInfo: {
    name: string;
    email: string;
    company?: string;
    phone?: string;
  };
  siteType: string;
  designType: string;
  budget: string;
  features: string[];
  total: number;
  maintenanceCost: number;
  createdAt: string;
  status: 'pending' | 'signed' | 'accepted' | 'rejected';
}

export default function DevisSignaturePage() {
  const params = useParams();
  const router = useRouter();
  const [devisData, setDevisData] = useState<DevisData | null>(null);
  const [signature, setSignature] = useState<string | null>(null);
  const [acceptedTerms, setAcceptedTerms] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    if (!params || typeof params.id !== 'string') return;
    // Charger les données du devis depuis localStorage si disponible
    const localDevis = localStorage.getItem('devisData');
    if (localDevis) {
      const parsed = JSON.parse(localDevis);
      setDevisData({
        id: params.id,
        devisNumber: localStorage.getItem('lastDevisNumber') || String(params.id),
        clientInfo: parsed.clientInfo,
        siteType: parsed.siteType,
        designType: parsed.designType,
        budget: `${parsed.total}€ HT`,
        features: parsed.features || [],
        total: parsed.total,
        maintenanceCost: parsed.maintenance ? 49 : 0,
        createdAt: new Date().toISOString(),
        status: 'pending',
      });
    } else {
      // Fallback mock
      setDevisData({
        id: String(params.id),
        devisNumber: '2025-0124-001',
        clientInfo: {
          name: 'Jean Dupont',
          email: 'jean.dupont@email.com',
          company: 'Entreprise ABC',
          phone: '06 12 34 56 78'
        },
        siteType: 'Site Multi-pages Professionnel',
        designType: 'Design sur-mesure',
        budget: '1150€ HT',
        features: [
          'Jusqu\'à 5 pages',
          'Design 100% sur-mesure',
          'Intégration de contenu',
          'Formation à la gestion',
          'Support prioritaire'
        ],
        total: 1150,
        maintenanceCost: 45,
        createdAt: new Date().toISOString(),
        status: 'pending'
      });
    }
  }, [params]);

  const handleSignDevis = async () => {
    if (!signature || !acceptedTerms || !devisData) {
      setError('Veuillez signer le devis et accepter les conditions');
      return;
    }

    setIsSubmitting(true);
    setError(null);

    try {
      // Stocker la signature dans localStorage pour la page de confirmation
      localStorage.setItem('devisSignature', signature);
      localStorage.setItem('devisSignedAt', new Date().toISOString());

      // Envoyer la signature
      const response = await fetch('/api/sign-devis', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          devisId: devisData.id,
          signature,
          clientInfo: devisData.clientInfo,
          signedAt: new Date().toISOString(),
        }),
      });

      if (response.ok) {
        // Rediriger vers la page de confirmation
        router.push(`/devis/signature/${devisData.id}/confirmation`);
      } else {
        throw new Error('Erreur lors de la signature');
      }
    } catch (error) {
      setError('Erreur lors de la signature du devis. Veuillez réessayer.');
      console.error('Erreur:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  if (error && !devisData) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <Card className="max-w-md">
          <CardContent className="p-6 text-center">
            <AlertCircle className="w-12 h-12 text-red-500 mx-auto mb-4" />
            <h2 className="text-xl font-bold mb-2">Erreur</h2>
            <p className="text-gray-600 mb-4">{error}</p>
            <Button asChild>
              <Link href="/">Retour à l'accueil</Link>
            </Button>
          </CardContent>
        </Card>
      </div>
    );
  }

  if (!devisData) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
          <h2 className="text-xl font-bold mb-2">Chargement du devis...</h2>
          <p className="text-gray-600">Veuillez patienter</p>
        </div>
      </div>
    );
  }

  if (devisData.status === 'signed') {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <Card className="max-w-md">
          <CardContent className="p-6 text-center">
            <CheckCircle2 className="w-12 h-12 text-green-500 mx-auto mb-4" />
            <h2 className="text-xl font-bold mb-2">Devis déjà signé</h2>
            <p className="text-gray-600 mb-4">
              Ce devis a déjà été signé. Vous recevrez une réponse sous 24h.
            </p>
            <Button asChild>
              <Link href="/">Retour à l'accueil</Link>
            </Button>
          </CardContent>
        </Card>
      </div>
    );
  }

  const today = new Date().toLocaleDateString('fr-FR');

  return (
    <div className="min-h-screen bg-background py-8">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold mb-2">Signature du Devis</h1>
          <p className="text-gray-600">
            Veuillez vérifier les détails et signer pour accepter ce devis
          </p>
        </div>

        {/* Devis Summary */}
        <Card className="mb-8">
          <CardHeader className="bg-primary text-primary-foreground">
            <div className="flex justify-between items-start">
              <div>
                <CardTitle className="text-2xl">DEVIS #{devisData.devisNumber}</CardTitle>
                <p className="mt-2 opacity-90">Matthéo Termine - Intégrateur Web</p>
              </div>
              <div className="text-right opacity-90">
                <p><strong>Date:</strong> {today}</p>
                <p><strong>Statut:</strong> En attente de signature</p>
              </div>
            </div>
          </CardHeader>

          <CardContent className="p-6">
            {/* Client Info */}
            <div className="mb-6">
              <h3 className="text-lg font-semibold mb-3 text-primary">Client :</h3>
              <div className="bg-gray-50 p-4 rounded-lg">
                <p><strong>Nom :</strong> {devisData.clientInfo.name}</p>
                {devisData.clientInfo.company && (
                  <p><strong>Entreprise :</strong> {devisData.clientInfo.company}</p>
                )}
                <p><strong>Email :</strong> {devisData.clientInfo.email}</p>
                {devisData.clientInfo.phone && (
                  <p><strong>Téléphone :</strong> {devisData.clientInfo.phone}</p>
                )}
              </div>
            </div>

            <Separator className="my-6" />

            {/* Project Details */}
            <div className="mb-6">
              <h3 className="text-lg font-semibold mb-3 text-primary">
                Projet : {devisData.siteType}
              </h3>
              <div className="grid md:grid-cols-2 gap-6">
                <div>
                  <h4 className="font-semibold mb-2">Prestations incluses :</h4>
                  <ul className="space-y-1">
                    {devisData.features.map((feature, index) => (
                      <li key={index} className="flex items-start">
                        <CheckCircle2 className="w-4 h-4 text-green-500 mr-2 mt-0.5 flex-shrink-0" />
                        <span className="text-sm">{feature}</span>
                      </li>
                    ))}
                  </ul>
                </div>
                <div className="bg-blue-50 p-4 rounded-lg">
                  <h4 className="font-semibold mb-2">Tarification :</h4>
                  <p className="text-2xl font-bold text-primary">{devisData.budget}</p>
                  <p className="text-sm text-gray-600 mt-1">
                    + Maintenance : {devisData.maintenanceCost}€/mois
                  </p>
                  <div className="mt-3 text-sm">
                    <p><strong>Modalités :</strong></p>
                    <p>• 30% à la signature</p>
                    <p>• 70% à la livraison</p>
                  </div>
                </div>
              </div>
            </div>

            <Separator className="my-6" />

            {/* Terms */}
            <div className="mb-6">
              <h3 className="text-lg font-semibold mb-3 text-primary">Conditions :</h3>
              <div className="bg-gray-50 p-4 rounded-lg text-sm space-y-2">
                <p>• Devis valable 30 jours à compter de la date d'émission</p>
                <p>• Délai de réalisation : 2-4 semaines selon la complexité</p>
                <p>• Maintenance et hébergement : engagement minimum 12 mois</p>
                <p>• Modifications majeures après validation : facturation supplémentaire</p>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Signature Section */}
        <Card className="mb-8">
          <CardHeader>
            <CardTitle className="text-center">Signature Électronique</CardTitle>
          </CardHeader>
          <CardContent className="space-y-6">
            <SignaturePad 
              onSignatureChange={setSignature}
              disabled={isSubmitting}
            />

            <div className="flex items-start space-x-3">
              <Checkbox
                id="terms"
                checked={acceptedTerms}
                onCheckedChange={(checked) => setAcceptedTerms(checked as boolean)}
                disabled={isSubmitting}
              />
              <label htmlFor="terms" className="text-sm leading-relaxed cursor-pointer">
                J'accepte les conditions générales de vente et confirme que les informations 
                de ce devis correspondent à ma demande. Je comprends qu'en signant ce document, 
                je m'engage à respecter les modalités de paiement et les conditions énoncées.
              </label>
            </div>

            {error && (
              <div className="bg-red-50 border border-red-200 rounded-lg p-4">
                <div className="flex items-center">
                  <AlertCircle className="w-5 h-5 text-red-500 mr-2" />
                  <p className="text-red-700 text-sm">{error}</p>
                </div>
              </div>
            )}

            <div className="text-center">
              <Button
                size="lg"
                onClick={handleSignDevis}
                disabled={!signature || !acceptedTerms || isSubmitting}
                className="w-full sm:w-auto"
              >
                {isSubmitting ? (
                  <>
                    <div className="mr-2 h-4 w-4 animate-spin rounded-full border-2 border-white border-t-transparent"></div>
                    Signature en cours...
                  </>
                ) : (
                  <>
                    <FileText className="mr-2 h-5 w-5" />
                    Signer et Valider le Devis
                  </>
                )}
              </Button>
            </div>

            <div className="text-center text-sm text-gray-500">
              <Clock className="w-4 h-4 inline mr-1" />
              Après signature, vous recevrez une confirmation et Matthéo vous recontactera sous 24h
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

'use client';

import { useSearchParams, useRouter } from 'next/navigation';
import { useState, useEffect, Suspense, useRef } from 'react';
import { Button } from '@/components/ui/button';
import { Separator } from '@/components/ui/separator';
import { Download, Send, ArrowLeft } from 'lucide-react';
import Link from 'next/link';
import jsPDF from 'jspdf';

interface DevisData {
  siteType: string;
  designType: string;
  technology?: string;
  features: string[];
  maintenance?: boolean;
  projectDescription?: string;
  clientInfo: {
    name: string;
    email: string;
    company?: string;
    phone?: string;
  };
  total: number;
}

function DevisValidationContent() {
  const searchParams = useSearchParams();
  const router = useRouter();
  const [devisData, setDevisData] = useState<DevisData | null>(null);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [devisNumber, setDevisNumber] = useState('');
  const devisRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    // Récupérer les données du devis depuis les paramètres URL ou localStorage
    const data = localStorage.getItem('devisData');
    if (data) {
      const parsedData = JSON.parse(data);
      setDevisData(parsedData);

      // Générer un numéro de devis unique
      const today = new Date();
      const year = today.getFullYear();
      const month = String(today.getMonth() + 1).padStart(2, '0');
      const day = String(today.getDate()).padStart(2, '0');
      const random = Math.floor(Math.random() * 1000).toString().padStart(3, '0');
      setDevisNumber(`${year}-${month}${day}-${random}`);
    }
  }, []);

  const handleDownloadPDF = async () => {
    if (!devisRef.current || !devisData) return;
    try {
      const pdf = new jsPDF('p', 'mm', 'a4');
      await pdf.html(devisRef.current, {
        margin: [10, 10, 10, 10],
        autoPaging: true,
        html2canvas: {
          scale: 1.5,
          useCORS: true,
          backgroundColor: '#fff',
        },
      });
      pdf.save(`Devis_${devisNumber}_${devisData.clientInfo.name.replace(/\s+/g, '_')}.pdf`);
    } catch (error) {
      console.error('Erreur lors de la génération du PDF:', error);
      alert('Erreur lors de la génération du PDF. Veuillez réessayer.');
    }
  };

  const handleSubmitDevis = async () => {
    if (!devisData || !devisRef.current) return;
    setIsSubmitting(true);
    try {
      // Générer le PDF du devis
      const pdf = new jsPDF('p', 'mm', 'a4');
      await pdf.html(devisRef.current, {
        margin: [10, 10, 10, 10],
        autoPaging: true,
        html2canvas: {
          scale: 1.5,
          useCORS: true,
          backgroundColor: '#fff',
        },
      });
      const pdfBlob = pdf.output('blob');
      // Envoyer le PDF par email au prestataire
      const formData = new FormData();
      formData.append('pdf', pdfBlob, `Devis_${devisNumber}_${devisData.clientInfo.name.replace(/\s+/g, '_')}.pdf`);
      formData.append('devisData', JSON.stringify(devisData));
      formData.append('devisNumber', devisNumber);
      const response = await fetch('/api/send-devis', {
        method: 'POST',
        body: formData,
      });
      if (response.ok) {
        router.push('/devis/confirmation');
      } else {
        throw new Error('Erreur lors de l\'envoi du devis');
      }
    } catch (error) {
      console.error('Erreur:', error);
      alert('Une erreur est survenue lors de l\'envoi du devis. Veuillez réessayer.');
    } finally {
      setIsSubmitting(false);
    }
  };

